import React, { Component } from 'react';
import './App.css';
import Customer from './components/Customer';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableHead from '@material-ui/core/TableHead';
import TableBody from '@material-ui/core/TableBody';
import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableCell';
import Circularprogress from '@material-ui/core/CircularProgress';
import { withStyles } from '@material-ui/core/styles';

const styles = theme => ({
  root : {
    width : '100%' , 
    marginTop : theme.spacing.unit * 3,
    overflowX : "auto"
  },
  table : {
    minWidth : 1080
  },
  progress : {
    margin: theme.spacing.unit * 2
  }
})

// componenet web문에서에서 보여주기 위한 기본적인 단위
class App extends Component {
  state = {
    customers : "",
    completed : 0
  }

  componentDidMount(){
    this.timer = setInterval(this.progress, 20);
    this.callApi()
      .then(res=> this.setState({customers: res}))
      .catch(err => console.log(err));
  }

  callApi = async () => {
    const response = await fetch("/api/customers");
    const body = await response.json();
    return body;
  }

  progress = () => {
    const { completed } = this.state;
    this.setState({completed : completed >= 100 ? 0 : completed +1});
  }
/*
  1) constructor()

  2) componenetwillMount()

  3) render()

  4) componenetDidMount()

*/

/*
    props or state => 가 변경되는 경우에는 shouldComponenetUpdate()

    리엑트는 상태를 변경하는 것을 알려주기때문에 상태만 관리 하면됨
*/ 
  render(){
    const { classes } = this.props;
    return(
      <Paper className={classes.root}>
          <Table className={classes.table}>
              <TableHead>
                <TableCell>번호</TableCell>
                <TableCell>아미지</TableCell>
                <TableCell>이름</TableCell>
                <TableCell>생년월일</TableCell>
                <TableCell>성별</TableCell>
                <TableCell>직업</TableCell>
              </TableHead>
              <TableBody>
                {this.state.customers ?  this.state.customers.map( c =>{ 
                  return ( <Customer key={c.id} id={c.id} image={c.image} name={c.name} birthday={c.birthday} gender={c.gender} job={c.job} />) 
                 }) : 
                 <TableRow>
                    <TableCell colSpan="6" align="center">
                      <Circularprogress className={classes.progress} variant="determinate"></Circularprogress>

                    </TableCell>

                 </TableRow>
                 }
              </TableBody>
          </Table>
      </Paper>
    )
  }
}

export default withStyles(styles)(App);
